import React, { useState, useEffect } from 'react';
import { Line } from 'react-chartjs-2';

const WeatherApp = () => {
  const [weatherData, setWeatherData] = useState(null);
  const [weatherSummary, setWeatherSummary] = useState([]);
  const [temperatureThreshold, setTemperatureThreshold] = useState(35); // Example threshold
  const [alertTriggered, setAlertTriggered] = useState(false);

  useEffect(() => {
    fetchWeatherData();
  }, []);

  const fetchWeatherData = async () => {
    try {
      const response = await fetch('https://api.openweathermap.org/data/2.5/weather?q=London&appid=485bc00cbf8fd5eb3dc717143b834c52'); // Replace with your API
      const data = await response.json();
      setWeatherData(data);
      const dailySummary = calculateDailySummary(data);
      setWeatherSummary([dailySummary]);
    } catch (error) {
      console.error('Error fetching weather data:', error);
    }
    console.log(weatherSummary)
  };

  useEffect(() => {
    if (weatherData && weatherData.main.temp > temperatureThreshold) {
      setAlertTriggered(true);
      console.log(`Alert! Temperature exceeded ${temperatureThreshold}째C.`);
    }
  }, [weatherData, temperatureThreshold]);

  const calculateDailySummary = (weatherData) => {
    return {
      date: new Date(weatherData.dt * 1000).toLocaleDateString(),
      avgTemp: weatherData.main.temp,
      maxTemp: weatherData.main.temp_max,
      minTemp: weatherData.main.temp_min,
      dominantCondition: weatherData.weather[0].main,
    };
  };

  const lineChartData = {
    labels: weatherSummary.map(summary => summary.date),
    datasets: [
      {
        label: 'Average Temperature (째C)',
        data: weatherSummary,
        backgroundColor: 'rgba(75, 192, 192, 0.6)',
        borderColor: 'rgba(75, 192, 192, 1)',
        fill: false,
      },
    ],
  };

  return (
    <div>
      <h1>Weather App</h1>
      {alertTriggered && <p>Alert! Temperature exceeded threshold.</p>}
      <div>
        
      </div>
    </div>
  );
};

export default WeatherApp;
