import { Line } from 'react-chartjs-2';
import React, { useEffect, useState } from 'react';
import { Chart, registerables } from 'chart.js';

Chart.register(...registerables); // Register chart.js components

const weather = () => {
  const [weatherData, setWeatherData] = useState(null);
  const [weatherSummary, setWeatherSummary] = useState(null);
  const [temperatureData, setTemperatureData] = useState([]);

  useEffect(() => {
    const fetchWeatherData = async () => {
      try {
        const response = await fetch('https://api.openweathermap.org/data/2.5/weather?q=London&appid=485bc00cbf8fd5eb3dc717143b834c52');
        const data = await response.json();
        setWeatherData(data);
        
        const dailySummary = calculateDailySummary(data);
        setWeatherSummary(dailySummary);

        // Prepare temperature data for chart
        setTemperatureData((prev) => [...prev, dailySummary.averageTemp]);
        
        // Log after setting state
        console.log(dailySummary); // This will show the correct summary
      } catch (error) {
        console.error('Error fetching weather data:', error);
      }
    };

    fetchWeatherData();
  }, []);

  const calculateDailySummary = (data) => {
    if (!data || !data.main) return null;

    // Example calculations, adjust as needed
    const averageTemp = data.main.temp; // Current temperature from the response
    const maxTemp = data.main.temp_max;
    const minTemp = data.main.temp_min;
    const humidity = data.main.humidity;
    
    return {
      averageTemp,
      maxTemp,
      minTemp,
      humidity,
      weatherDescription: data.weather[0]?.description || 'No description',
    };
  };

  useEffect(() => {
    // Log the weather summary whenever it updates
    if (weatherSummary) {
      console.log('Updated Weather Summary:', weatherSummary);
    }
  }, [weatherSummary]);

  // Prepare chart data
  const chartData = {
    labels: temperatureData.map((_, index) => `Day ${index + 1}`), // Label for each day
    datasets: [
      {
        label: 'Average Temperature (°K)',
        data: temperatureData,
        fill: false,
        backgroundColor: 'rgba(75, 192, 192, 0.6)',
        borderColor: 'rgba(75, 192, 192, 1)',
      },
    ],
  };

  const options = {
    responsive: true,
    scales: {
      y: {
        beginAtZero: true,
        title: {
          display: true,
          text: 'Temperature (°K)',
        },
      },
    },
  };

  return (
    <div>
      <h1>Weather in London</h1>
      {weatherSummary ? (
        <div>
          <p>Average Temperature: {weatherSummary.averageTemp}°K</p>
          <p>Max Temperature: {weatherSummary.maxTemp}°K</p>
          <p>Min Temperature: {weatherSummary.minTemp}°K</p>
          <p>Humidity: {weatherSummary.humidity}%</p>
          <p>Condition: {weatherSummary.weatherDescription}</p>
        </div>
      ) : (
        <p></p>
      )}

    </div>
  );
};

export default weather;
